function str_pad_left(string,pad,length) {
    return (new Array(length+1).join(pad)+string).slice(-length);
}

// Make pretty format of the countdown
var minutes = Math.floor(msg.payload / 60);
var seconds = msg.payload - minutes * 60;
var finalTime = str_pad_left(minutes,'0',2)+':'+str_pad_left(seconds,'0',2);

msg.payload = finalTime;

return msg;

if (msg.mash_label == 'Stop Mash Timer'){
    var seconds = global.get('mash_length') * 60;
}
else {
    var seconds = global.get('boil_length') * 60;
}

var timer_status = msg.timer;

msg.payload = "{\"payload\": " + timer_status + ", \"timeout\":" + seconds + ", \"warning\": 30}";

return msg;

var beer_name = msg.payload.RECIPES.RECIPE[0].NAME[0];
var hops = msg.payload.RECIPES.RECIPE[0].HOPS[0].HOP;
var water = parseFloat(msg.payload.RECIPES.RECIPE[0].WATERS[0].WATER[0].AMOUNT[0]);
var mash_length = parseFloat(msg.payload.RECIPES.RECIPE[0].MASH[0].MASH_STEPS[0].MASH_STEP[0].STEP_TIME[0]);
var boil_length = parseFloat(msg.payload.RECIPES.RECIPE[0].BOIL_TIME[0]);
var mash_temp = parseFloat(msg.payload.RECIPES.RECIPE[0].MASH[0].MASH_STEPS[0].MASH_STEP[0].DISPLAY_STEP_TEMP[0]);
var misc_add = msg.payload.RECIPES.RECIPE[0].MISCS[0].MISC;

let mash_add = [];
let boil_add = [];

misc_add.forEach(function(item, index, array) {
    if (item.USE[0] == 'Mash'){
        mash_add.push(item);
    }
    else {
        item.TIME[0] = Math.trunc(item.TIME[0]);
        boil_add.push(item);
    }
});

hops.forEach(function(item, index, array) {
    if (item.USE[0] == 'Mash'){
        mash_add.push(item);
    }
    else {
        item.TIME[0] = Math.trunc(item.TIME[0]);
        boil_add.push(item);
    }
});

boil_add.sort(function(a, b){return b.TIME - a.TIME});

global.set('beer_name', beer_name);
global.set('boil_add', boil_add);
global.set('water', water);
global.set('mash_length', mash_length);
global.set('boil_length', boil_length);
global.set('mash_temp', mash_temp);
global.set('mash_add', mash_add);
//msg.ui_control.options.title.text = 'Redtide Home Brewery - ' + name;

return msg;

// Assumes 1 second of iteration time
// Based on Justin Angevaare's PID
// November 2020

// Manually specify update interval
msg.interval = 2000;

// Set output max
msg.max_output = 100

// Get Temperature and calculate error
msg.mash_target = flow.get('mash_target')
msg.error = msg.mash_target - global.get('kettle-temp');

// Update outer integrator
// For purposes of integrator, bound error by [-1, 1]
msg.integrator = flow.get('integrator');
msg.integrator += (msg.interval / 1000) * Math.max(Math.min(msg.error, 1), -1);

// Calculate integrator max
msg.integrator_max = msg.max_output / flow.get('i');

// Bound total outer integrator by absolute maximum
msg.integrator = Math.max(Math.min(msg.integrator, msg.integrator_max), -msg.integrator_max);

// Output updated integrator to flow context
flow.set('integrator', msg.integrator);

// Calculate proportional action
msg.output = msg.error * flow.get('p');

// Calculate integral action
msg.output += msg.integrator * flow.get('i');

// Calculate derivative action
msg.derivative = (msg.error - flow.get('last_error')) / (msg.interval/1000);
msg.output += msg.derivative * flow.get('d');

// Bound output by [0, max_output]
msg.output = Math.max(Math.min(msg.output, msg.max_output), 0);

// Output updated last_error to flow context
msg.last_error = msg.error;
flow.set('last_error', msg.last_error);

// Set payload to element output
msg.payload = msg.output;
return msg;
